import React, { useEffect, useRef } from 'react';
import { Box, Paper, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import { stations } from '../data/stations';

const StationMap = ({ currentLocation, nearestStation }) => {
  const canvasRef = useRef(null);
  
  useEffect(() => {
    if (!canvasRef.current || !currentLocation) return;
    
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');
    const width = canvas.width;
    const height = canvas.height;
    
    // Clear canvas
    ctx.clearRect(0, 0, width, height);
    
    // Find map boundaries
    let minLat = Infinity, maxLat = -Infinity, minLon = Infinity, maxLon = -Infinity;
    stations.forEach(station => {
      minLat = Math.min(minLat, station[0]);
      maxLat = Math.max(maxLat, station[0]);
      minLon = Math.min(minLon, station[1]);
      maxLon = Math.max(maxLon, station[1]);
    });
    
    // Add some padding
    const padding = 0.01;
    minLat -= padding;
    maxLat += padding;
    minLon -= padding;
    maxLon += padding;
    
    // Function to convert coordinates to canvas position
    const coordToCanvas = (lat, lon) => {
      const x = ((lon - minLon) / (maxLon - minLon)) * width;
      const y = height - ((lat - minLat) / (maxLat - minLat)) * height;
      return { x, y };
    };
    
    // Draw train line
    ctx.beginPath();
    ctx.strokeStyle = '#3498db';
    ctx.lineWidth = 4;
    
    const firstStation = coordToCanvas(stations[0][0], stations[0][1]);
    ctx.moveTo(firstStation.x, firstStation.y);
    
    stations.forEach((station, index) => {
      if (index === 0) return;
      const pos = coordToCanvas(station[0], station[1]);
      ctx.lineTo(pos.x, pos.y);
    });
    
    ctx.stroke();
    
    // Draw stations
    stations.forEach(station => {
      const pos = coordToCanvas(station[0], station[1]);
      
      // Station circle
      ctx.beginPath();
      ctx.fillStyle = '#2c3e50';
      ctx.arc(pos.x, pos.y, 5, 0, Math.PI * 2);
      ctx.fill();
      
      // Highlight nearest station
      if (nearestStation && station[2] === nearestStation.name) {
        ctx.beginPath();
        ctx.strokeStyle = '#e74c3c';
        ctx.lineWidth = 2;
        ctx.arc(pos.x, pos.y, 8, 0, Math.PI * 2);
        ctx.stroke();
      }
    });
    
    // Draw user location
    const userPos = coordToCanvas(currentLocation.latitude, currentLocation.longitude);
    ctx.beginPath();
    ctx.fillStyle = '#e74c3c';
    ctx.arc(userPos.x, userPos.y, 6, 0, Math.PI * 2);
    ctx.fill();
    
    // Draw line between user and nearest station
    if (nearestStation) {
      const nearestStationData = stations.find(s => s[2] === nearestStation.name);
      if (nearestStationData) {
        const stationPos = coordToCanvas(nearestStationData[0], nearestStationData[1]);
        
        ctx.beginPath();
        ctx.strokeStyle = '#e74c3c';
        ctx.lineWidth = 2;
        ctx.setLineDash([5, 3]);
        ctx.moveTo(userPos.x, userPos.y);
        ctx.lineTo(stationPos.x, stationPos.y);
        ctx.stroke();
        ctx.setLineDash([]);
      }
    }
    
  }, [currentLocation, nearestStation]);
  
  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5, delay: 0.2 }}
    >
      <Paper elevation={3} sx={{ p: 3, mb: 4, borderRadius: 2 }}>
        <Typography variant="h5" component="h3" gutterBottom>
          Mapa de Estaciones
        </Typography>
        <Box sx={{ 
          width: '100%', 
          height: '400px',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          overflow: 'hidden',
          borderRadius: 1,
          bgcolor: '#f8f9fa'
        }}>
          <canvas 
            ref={canvasRef} 
            width={800} 
            height={400} 
            style={{ maxWidth: '100%', height: 'auto' }}
          />
        </Box>
        <Box sx={{ mt: 2, display: 'flex', justifyContent: 'center', gap: 3 }}>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#e74c3c', mr: 1 }} />
            <Typography variant="body2">Tu ubicación</Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Box sx={{ width: 12, height: 12, borderRadius: '50%', bgcolor: '#2c3e50', mr: 1 }} />
            <Typography variant="body2">Estaciones</Typography>
          </Box>
          <Box sx={{ display: 'flex', alignItems: 'center' }}>
            <Box sx={{ width: 12, height: 12, borderRadius: '50%', border: '2px solid #e74c3c', mr: 1 }} />
            <Typography variant="body2">Estación más cercana</Typography>
          </Box>
        </Box>
      </Paper>
    </motion.div>
  );
};

export default StationMap;
